export default class Conversor extends Component {

  constructor(props){
    super(props)

    this.state = {
      moedaA_valor: "",
      moedaB_valor: 0,
    }
  }


    render(){
        return(

          <div className="conversor">

        <h2>{this.props.moedaA} para {this.props.moedaB}</h2>
          <input type="text"/>
          <input type="button" value="Converter"/>
          <h2>Valor convertido: </h2>

          </div>

        )
    }
}
