import React, { Component } from 'react';
import { createPortal } from 'react-dom';

export default class Conversor extends Component {

  constructor(props){
    super(props)

    this.state = {
      moedaA_valor: "",
      moedaB_valor: 0,  
    }

    this.converter = this.converter.bind(this);

  }

  converter(){
    var de_para = '$(this.props.moedaA)_$(this.props.moedaB)';
    var url = `https://free.currconv.com/api/v7/convert?q=${de_para}&compact=ultra&apiKey=9c254009b27837826d14`;


    fetch(url)
    .then(res=> {

      return res.json()
    })
    .then(json => {
      var cotacao = json[de_para].val
      var moedaB_valor = ( parseFloat( this.state.moedaA_valor) * cotacao).toFixed(2)
      this.setState((moedaB_valor))
    })
  }


    render(){
        return(

          <div className="conversor">

        <h2>{this.props.moedaA} para {this.props.moedaB}</h2>
          <input type="text" onChange={(event)=>{this.setState({moedaA_valor:event.target.value})}}/>
          <input type="button" value="Converter" onClick={this.converter}/>
        <h2>Valor convertido: {this.state.moedaB_valor}</h2>

          </div>

        )
    }
}
